import { createSlice } from "@reduxjs/toolkit";

export const initialState = {
  items: [],
  count: 0
}

const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    add: (state, { payload }) => {
      const existingItem = state.items.find(item => item._id === payload._id)
      if (existingItem) {
        const count = state.count += payload.qty
        return {
          ...state,
          count,
          items: state.items.map(item => (
            item._id === existingItem._id ? payload : item
          ))
        }
      } else {
        state.items.push(payload)
        state.count += payload.qty
      }
    },
    remove: (state, { payload }) => {
      state.items.filter(item => item._id !== payload._id)
    }
  }
})

export const { add, remove } = cartSlice.actions

export default cartSlice.reducer
