import { createSlice } from "@reduxjs/toolkit";

export const initialState = {
  cart: []
}

const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    add: (state, { payload }) => {
      state.cart.push(payload)
    }
  }
})

export const { add } = cartSlice.actions

export default cartSlice.reducer
