import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Box, Image, SimpleGrid, Spinner } from '@chakra-ui/react'

import Alert from '../components/Alert'
import { fetchProduct } from '../features/products/productSlice'
import { Store } from '../store'

const Product = () => {
  const { id }: any = useParams()
  const dispatch = useDispatch()
  const { product, status, error } = useSelector((state: Store) => state.productReducer)

  useEffect(() => {
    dispatch(fetchProduct(id))
  }, [dispatch])

  if (error) {
    return <Alert />
  }

  return (
    product ?
      <SimpleGrid columns={3} spacing='30px'>
        <Image src={product.image} />
        <Box>
          <Box fontSize='xl' fontweight='bold'>{product.description}</Box>
        </Box>
      </SimpleGrid>
    : <Spinner size='lg' />
  )
}

export default Product
