import { createEntityAdapter, createSlice } from "@reduxjs/toolkit";

export const initialState = {
  items: [],
}

const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    add: ({ items }, { payload }) => {
      const existingItem = items.find(item => item._id === payload.id)
      if (existingItem) {
        existingItem = payload
      } else {
        items.push(payload)
      }
    },
    remove: (state, { payload }) => {
      return state.items.filter(item => item._id !== payload._id)
    }
  }
})

export const { add, remove, update } = cartSlice.actions

export const itemCount = () => {
  console.log(cartSlice)
  const { items } = cartSlice.reducer
  let count = 0
  items.forEach(item => {
    count += item.qty
  })
  return count
}

export default cartSlice.reducer
