import React from 'react'
import { StarIcon } from '@chakra-ui/icons'
import styled from '@emotion/styled'

interface Props {
  value: number
}

const StyledStar = styled(StarIcon)`
  background-color: ${p => p.value >= p.i ? 'teal' : p.value >= p.value - p.i ? 'transparent' :  'teal'};
`

const Rating = ({ value }: Props) => {
  return (
    <>
      <StarIcon i={1} value={value} />
      <StarIcon i={2} value={value} ml={1} />
      <StarIcon i={3} value={value} ml={1} />
      <StarIcon i={4} value={value} ml={1} />
      <StarIcon i={5} value={value} ml={1} />
    </>
  )
}

export default Rating
